import React from 'react';
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  SafeAreaView,
  TouchableOpacity,
  KeyboardAvoidingView,
  ActivityIndicator,
  Keyboard,
  TouchableWithoutFeedback,
  Platform,
  Image
} from 'react-native';

import firebase from "firebase/compat/app"
import "firebase/compat/auth"
import "firebase/compat/firestore"

import * as Facebook from 'expo-facebook'
import * as Google from 'expo-google-app-auth';
import * as Segment from 'expo-analytics-segment';
import Icon from 'react-native-vector-icons/Ionicons'
import LockIcon from 'react-native-vector-icons/Fontisto'
import IconEmail from 'react-native-vector-icons/Zocial'


class SignUpScreen extends React.Component {
  state = { r: '', email: '', password: '', errorMessage: '', loading: false, displayName:'' , photoURL:'',
  familyName: '',givenName: ''};
  
  onLoginSuccess() {
    this.props.navigation.navigate('App');
  }


  onLoginFailure(errorMessage) {
    this.setState({ error: errorMessage, loading: false });
  }
  renderLoading() {

    if (this.state.loading) {
      return (
        <View>
          <ActivityIndicator size={'large'} />
        </View>
      );
    }
  }
  async signInWithEmail() {
    await firebase
      .auth()
      .createUserWithEmailAndPassword(this.state.email, this.state.password)
      .then(this.onLoginSuccess.bind(this))
      .catch(error => {
        let errorCode = error.code;
        let errorMessage = error.message;
        if (errorCode == 'auth/weak-password') {
          this.onLoginFailure.bind(this)('Weak Password!');
        } else {
          this.onLoginFailure.bind(this)(errorMessage);
        }
      });
    Segment.identify(this.state.email);
    Segment.trackWithProperties("User SignIn", {
      accountType: "CustomEmailAuth",
      email: this.state.email,
      displayName:this.state.displayName,
      photoURL:this.state.photoURL

    });

  }

  render() {
    return (
      <TouchableWithoutFeedback
        onPress={() => {
          Keyboard.dismiss();
        }}
      >
        <SafeAreaView style={{ flex: 1 }}>
          <KeyboardAvoidingView style={styles.container} behavior="padding">
            <Image style={styles.logo} source={require('../images/logo1.png')} />

            <Text style={{ fontSize: 32, fontWeight: '700', color: 'green', padding: 20 }}>
              Sign Up
            </Text>
            <View>
              <View style={styles.v}>
                <Icon name={'md-person'} size={21} color={'#2E922E'}
                  style={styles.inputIcon} />
                {/* <View style={styles.form}> */}
                <TextInput
                  style={styles.input}
                  placeholder="Name"
                  placeholderTextColor="#B1B1B1"
                  returnKeyType="next"
                  textContentType="name"
                  value={this.state.displayName}
                  onChangeText={displayName => this.setState({ displayName })}
                /></View>
              <View style={styles.v}>
                <IconEmail name={'email'} size={21} color={'#2E922E'}
                  style={styles.inputIcon} />
                <TextInput
                  style={styles.input}
                  placeholder="Email"
                  placeholderTextColor="#B1B1B1"
                  returnKeyType="next"
                  keyboardType="email-address"
                  textContentType="emailAddress"
                  value={this.state.email}
                  onChangeText={email => this.setState({ email })}
                />
              </View>
              <View style={styles.v}>
                <LockIcon name={'locked'} size={21} color={'#2E922E'}
                  style={styles.inputIcon} />
                <TextInput
                  style={styles.input}
                  placeholder="Password"
                  placeholderTextColor="#B1B1B1"
                  returnKeyType="done"
                  textContentType="newPassword"
                  secureTextEntry={true}
                  value={this.state.password}
                  onChangeText={password => this.setState({ password })}
                />
              </View>
            </View>
            {this.renderLoading()}
            <Text
              style={{
                fontSize: 18,
                textAlign: 'center',
                color: 'red',
                width: '80%'
              }}
            >
              {this.state.error}
            </Text>

            <TouchableOpacity activeOpacity={.5} style={styles.btnLogin}
              // style={{ width: '20%', marginTop: 20  }}
              onPress={() => this.signInWithEmail()}>
              <Text style={styles.textsubmit}>Sign Up</Text>
            </TouchableOpacity>


            <View style={{ marginTop: 15, flexDirection: 'row' }}>
              <Text style={{ fontWeight: "200", fontSize: 17, textAlign: "center" }}>Already have an account?</Text>
              <TouchableOpacity onPress={() => { this.props.navigation.navigate('SignIn') }}>
                <Text style={{ fontWeight: "200", fontSize: 17, textAlign: "center", fontWeight: 'bold', color: '#2E922E' }}> Login Now </Text>
              </TouchableOpacity>
            </View>
          </KeyboardAvoidingView>
        </SafeAreaView>
      </TouchableWithoutFeedback>
    );
  }
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "column",
    alignItems: "center"
  },
  form: {
    width: "86%",
    marginTop: 15
  },
  logo: {
    marginTop: 100,
    height: 100,
    width: 100
  },
  input: {
    fontSize: 20,
    borderColor: "#707070",
    borderBottomWidth: 1,
    paddingBottom: 1.5,
    marginTop: 25.5,
    borderRadius: 45,
    fontWeight: 'bold',
    color: 'black',
    paddingLeft: 48,
    marginHorizontal: 25,
  },
  button: {
    backgroundColor: "#3A559F",
    height: 44,
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
    borderRadius: 22
  },
  googleButton: {
    backgroundColor: "#FFFFFF",
    height: 44,
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
    borderRadius: 22,
    borderWidth: 1,
    borderColor: "#707070",
    width: 320,
  },
  pass: {
    height: 20,
    width: 20
  },
  backgroundContainer: {
    flex: 1,
    width: null,
    height: null,
    justifyContent: 'center',
    alignItems: 'center',
  },
  logoLogin: {
    width: 120,
    height: 120,
  },
  logoContainer: {
    alignItems: 'center',
    marginBottom: 50
  },
  logoText: {
    color: 'white',
    fontSize: 26,
    fontWeight: '500',
    marginTop: 10,
    opacity: 0.8
  },
  inputUsername: {
    width: 200,
    height: 45,
    borderRadius: 45,
    fontSize: 16,
    fontWeight: 'bold',
    backgroundColor: '#e3e3e3',
    color: 'black',
    paddingLeft: 48,
    marginTop: 15,
    marginHorizontal: 25,
    opacity: 0.5
  },
  inputIcon: {
    position: 'absolute',
    top: 21,
    left: 43
  },
  btnEye: {
    position: 'absolute',
    top: 24,
    right: 43
  },
  btnLogin: {
    width: 320,
    height: 45,
    borderRadius: 45,
    backgroundColor: '#2E922E',
    justifyContent: 'center',
    marginTop: 30,
  },
  textsubmit: {
    color: 'white',
    // fontSize: 18,
    textAlign: 'center',
    fontWeight: 'bold',
    letterSpacing: 0.5,
    fontSize: 16,
    // color: '#707070'
  },
  textLogin: {
    color: 'white',
    fontSize: 26,
    fontWeight: 'bold',
    marginTop: 30,
  },
  v: {
    width: 350
  }
});
export default SignUpScreen;
