import React, { Component } from "react";
import {
  SafeAreaView, Button, StyleSheet,
  Text,
  View,
  TouchableOpacity,
  Image,
  Alert,
  ScrollView,
  FlatList,
} from 'react-native';
import Zocial from 'react-native-vector-icons/Zocial'
import EntypoIcon from 'react-native-vector-icons/Entypo'
import Ionicons from 'react-native-vector-icons/Ionicons'
import AntDesign from 'react-native-vector-icons/AntDesign'
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons'

class CartProducts extends Component {


  render() {
    let data = this.props.products
    console.log(this.props.TotalAmount)
    return (
      <View style={styles.container}>
        <FlatList style={styles.list}
          contentContainerStyle={styles.listContainer}
          data={this.props.products.products}

          horizontal={false}
          numColumns={2}

          keyExtractor={(item) => {
            return item.id;
          }}
          ItemSeparatorComponent={() => {
            return (
              <View style={styles.separator} />
            )
          }}
          renderItem={(post) => {
            const item = post.item;
            return (
              <View style={styles.card}>

                <View style={styles.cardHeader}>

                  <View>

                    <SafeAreaView style={styles.socialBarButton}>
                      <EntypoIcon name="shop" size={23} color={'#2E922E'} style={{ padding: 5 }} />
                      <Text style={styles.shop}>{"  "}{item.provider}</Text>
                    </SafeAreaView>

                    <SafeAreaView style={styles.socialBarButton}
                    >
                      <Zocial name="cart" size={23} color={'#2E922E'} style={{ padding: 5 }} />
                      <Text >{"  "}</Text>
                      <Text style={styles.shop}>
                        {item.name}</Text>
                    </SafeAreaView>

                    <SafeAreaView style={styles.socialBarButton}>
                      <Ionicons name="pricetags-outline" size={23} color={'#2E922E'} style={{ padding: 5 }} />
                      <Text style={styles.shop}>{"  "}{item.price}{" ₪"}</Text>
                    </SafeAreaView>

                    <View style={styles.socialBarSection}>


                      <TouchableOpacity style={styles.socialBarButton}
                        onPress={() => {
                          this.props.onPressMinus(item);
                        }}
                      >
                        <AntDesign name="minuscircleo" size={23} color={'#2E922E'} style={{ padding: 5 }} />

                      </TouchableOpacity>

                      <TouchableOpacity style={styles.socialBarButton}
                        onPress={() => {
                          this.props.onPressPlus(item);
                        }}
                      >
                        <AntDesign name="pluscircleo" size={23} color={'#2E922E'} style={{ padding: 5 }} />
                      </TouchableOpacity>

                      <Text style={{
                        color: "purple",
                        padding: 5
                      }}>{"× "}{item.quantity}</Text>

                    </View>

                    <View style={styles.socialBarContainer}>

                      <View style={styles.socialBarSection}>
                        <TouchableOpacity style={styles.socialBarButton}
                          onPress={() => {
                            this.props.onPress(item);
                          }}
                        >
                          <MaterialCommunityIcons name="cart-remove" size={23} color={'#2E922E'} style={{ padding: 5 }} />
                          <Text style={{
                            color: "purple",
                            padding: 5
                          }}>
                            Delete</Text>
                        </TouchableOpacity>

                      </View>
                    </View>




                  </View>






                </View>
              </View>
            )
          }}
        />


        <View style={styles.cardFooter}>
          <Text style={styles.buyNow}>Total Price = {this.props.TotalAmount}</Text>


          <TouchableOpacity style={styles.socialBarButton}
            onPress={() => { this.props.navigation.navigate("CheckOut") }}
          >
            <Image style={styles.icon} source={{ uri: 'https://cdn-icons-png.flaticon.com/512/6313/6313304.png' }} />
            <Text style={[styles.socialBarLabel, styles.buyNow]}>  CheckOut  </Text>
          </TouchableOpacity>
        </View>

      </View>
    );
  }

}

export default CartProducts;

const styles = StyleSheet.create({
  container: {
    flex: 2,
    marginTop: 20,
  },
  list: {
    paddingHorizontal: 5,
    backgroundColor: "#E6E6E6",
  },
  listContainer: {
    flexDirection: 'column',
    alignItems: 'center'
  },
  separator: {
    marginTop: 10,
  },
  /******** card **************/
  card: {
    shadowColor: '#00000021',
    shadowOffset: {
      width: 2
    },
    shadowOpacity: 0.5,
    shadowRadius: 4,
    marginVertical: 8,
    backgroundColor: "white",
    flexBasis: '47%',
    marginHorizontal: 5,
  },
  cardHeader: {
    paddingVertical: 17,
    paddingHorizontal: 16,
    borderTopLeftRadius: 1,
    borderTopRightRadius: 1,
    flexDirection: 'column',
    justifyContent: 'space-between',
  },
  cardContent: {
    paddingVertical: 12.5,
    paddingHorizontal: 16,
  },
  cardFooter: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingTop: 20,
    paddingBottom: 25,
    paddingHorizontal: 20,
    borderBottomLeftRadius: 1,
    borderBottomRightRadius: 1,
  },
  cardImage: {
    flex: 1,
    height: 250,
    width: null,
  },
  /******** card components **************/
  title: {
    fontSize: 18,
    flex: 1,
  },
  price: {
    fontSize: 16,
    color: "green",
    marginTop: 5
  },
  shop: {
    fontSize: 18,
    flex: 1,
    color: "purple",
  },
  total: {
    fontSize: 16,
    color: "green",
    marginTop: 5,
    marginLeft: "30%",
    flexDirection: 'row',
    justifyContent: 'center',
    textAlign: "center",

  },
  buyNow: {
    color: "purple",
  },
  icon: {
    width: 25,
    height: 25,
  },
  /******** social bar ******************/
  socialBarContainer: {
    // justifyContent: 'center',
    // alignItems: 'center',
    flexDirection: 'column',
    flex: 1
  },
  socialBarSection: {
    marginTop: 10,
    flexDirection: 'row',
    flex: 2,
    justifyContent: 'space-between',

  },
  socialBarlabel: {
    alignSelf: 'flex-end',
    justifyContent: 'space-between',
  },
  socialBarButton: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    // alignItems: 'center',
  }
});
